FROM TBL_BUYCHAPTER
WHERE USER_NUMBER = #{userNumber}

SELECT BUYCHAPTER_NUMBER, NOVEL_NUMBER, SUBNOVEL_NUMBER, USER_NUMBER, BUYCHAPTER_COIN, BUYCHAPTER_DATETIME, BUYCHAPTER_STATUS
