mapper.select:
SELECT SUPPORT_NUMBER, SUPPORT_SPONSER, SUPPORT_RECEIVER, NOVEL_NUMBER, SUPPORT_COIN, SUPPORT_DATE
FROM TBL_SUPPORT
WHERE USER_NUMBER = #{userNumber}

SELECT SUPPORT_NUMBER, SUPPORT_SPONSER, SUPPORT_RECEIVER, NOVEL_NUMBER, SUPPORT_COIN, SUPPORT_DATE
FROM TBL_SUPPORT
WHERE NOVEL_NUMBER = #{novelNumber};
